import React from 'react';
import CoinCard from './CoinCard.jsx'
import Chart from './Chart.jsx'

let coinAndPrice = [];
let coinAndHistory = [];

//TODO: break-out coinAndPrice logic
function App(props){
        let match = coinAndPrice.filter(element => {
            if(element.coin == props.coin){
                return element
            }
        });
        if(match.length === 0){
            let componentKey = Math.random() * Math.random();
            if(props.type == 'spot'){
                coinAndPrice.push({
                    key: componentKey,
                    coin: props.coin,
                    price: props.price
                })
            }
            if(props.type == 'history'){
                coinAndHistory.push({
                    key: componentKey,
                    coin: props.coin,
                    history: props.history
                })
            }
        } else {
            if(props.type == 'spot'){
                match[0].price = props.price;
            }
            if(props.type == 'history'){
                match[0].history = props.history;
            }
        };
        return (
            <div>{coinAndPrice.map(element =>
                <CoinCard
                    key={element.key}
                    coin={element.coin}
                    price={element.price}
                />
            )} {coinAndHistory.map(element =>
                <Chart
                    key={element.key}
                    coin={element.coin}
                    history={element.history}
                />
            )}</div>
        )
    }

